declare type AdjacentPositionType = 'up' | 'down' | 'left' | 'right';

declare type TileType = {
  type: TileTypeType;
  id: string;
  x: number;
  y: number;
  size: {
    width: number;
    height: number;
  };
  tall: number;
  active: boolean;
  adjacent: boolean;
  adjacentPosition: AdjacentPositionType;
  discovered: boolean;
  walkable: boolean;
};
